import axios from 'axios';

// הגדרת כתובת ה-API כ-default
const apiUrl = "http://localhost:5073"; // אם השרת פועל על HTTP
axios.defaults.baseURL = apiUrl;

axios.interceptors.response.use(
  response => response,
  error => {
      console.error('Error occurred:', error);
      if (error.response && error.response.status === 401) {
          window.location.href = '/login'; // הפנה לדף התחברות
      }
      return Promise.reject(error);
  }
);

export default {
  getTasks: async () => {
    const response = await axios.get('/items'); // שינוי ל-GET
    return response.data;
  },

  // addTask: async (name) => {
  //   console.log('addTask', name);
  //   const result = await axios.post('/items', { name });
  //   return result.data;
  // },
  addTask: async(name) => {
    console.log('addTask', name);
    try {
        const result = await axios.post(`${apiUrl}/items`, { name });
        return result.data;
    } catch (error) {
        console.error("Error adding task:", error.message);

        throw error; // זרוק את השגיאה כדי שתוכל לטפל בה במקום אחר
    }
},

  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });
    const result = await axios.put(`/items/${id}`, { isComplete });
    return result.data;
  },


  deleteTask: async (id) => {
    console.log('deleteTask', id);
    await axios.delete(`/items/${id}`);
  }
};





